/**
 * Handles requests for the application home page.
 */
@Controller
public class HomeController {
	
	private static final Logger logger = LoggerFactory.getLogger(HomeController.class);
	
	@Autowired
	TypeService typeService;
	
	@Autowired
	UserService userService;
	
	@Autowired
	VendorService vendorService;
	
	@Autowired
	TransactionService transactionService;
	
	@Autowired
	QRCodeService qrCodeService;
	
	@Autowired
	VendorQRCodeService vendorQrCodeService;
	
	
	@Autowired(required = true)
	public HttpServletRequest request;
	
	/**
	 * Simply selects the home view to render by returning its name.
	 */
	@RequestMapping(value = "/", method = RequestMethod.GET)
	public String home(Locale locale, Model model) {
		logger.info("Welcome home! The client locale is {}.", locale);
		
		Date date = new Date();
		DateFormat dateFormat = DateFormat.getDateTimeInstance(DateFormat.LONG, DateFormat.LONG, locale);
		
		String formattedDate = dateFormat.format(date);
		
		model.addAttribute("serverTime", formattedDate );
		
		return "home";
	}
	
	@RequestMapping(value = "/registration", method = RequestMethod.GET)
	public String registrationScreen(Model model) {
		List<Type> types = typeService.findAllType();
		model.addAttribute("types", types);
		List<Vendor> storeVendors = vendorService.findByType(typeService.findByName("Store"));
		model.addAttribute("storeVendors", storeVendors);
		List<Vendor> customerVendors = vendorService.findByType(typeService.findByName("Customer"));
		model.addAttribute("customerVendors", customerVendors);
	    return "registration";
	}
	
	@RequestMapping(value = "/listusers", method = RequestMethod.GET)
	public String listUsers(Model model) {
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			List<User> users = userService.findAll();
			model.addAttribute("users", users);
		    return "listusers";
		} else {
			return "login";
		}
		
	}
	
	@RequestMapping(value = "/listvendors", method = RequestMethod.GET)
	public String listVendor(Model model) {
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			List<Vendor> vendors = vendorService.findAll();
			model.addAttribute("vendors", vendors);
		    return "listvendors";
		} else {
			return "login";
		}
		
	}
	
	@RequestMapping(value = "/addvendor", method = RequestMethod.GET)
	public String addVendor(Model model) {
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			List<Type> types = typeService.findAllType();
			model.addAttribute("types", types);
		    return "addvendor";
		} else {
			return "login";
		}
	}
	
	@RequestMapping(value = "/viewdetail", method = RequestMethod.GET)
	public String viewdetail(Model model, HttpServletRequest request) {
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			String userid = request.getParameter("userid");
			if (userid != null && !userid.equals("")) {
				User user = userService.findByUserid(Long.parseLong(userid.toString()));
				if (user.getType().getName().equals("Customer")) {
					user.setBalance("" + WebAPICall.getCustomerBalance(user.getBcaddress()));
				} else if (user.getType().getName().equals("Store")) {
					user.setBalance("" + WebAPICall.getStoreBalance(user.getBcaddress()));
				}
				model.addAttribute("user", user);
				
				QRCode qrCode = qrCodeService.findByUser(user);
				if (qrCode != null) {
					StringBuilder sb = new StringBuilder();
					sb.append("data:image/png;base64,");
					sb.append(StringUtils.newStringUtf8(Base64.encodeBase64(qrCode.getCode(), false)));
					model.addAttribute("qrcode", sb.toString());
				} else {
					String noqrCode = "data:image/png;base64,";
					model.addAttribute("qrcode", noqrCode);
				}
				
				return "viewdetail";
			}
			
		    return "listusers";
		} else {
			return "login";
		}
	}
	
	@RequestMapping(value = "/viewvendordetail", method = RequestMethod.GET)
	public String viewVendorDetail(Model model, HttpServletRequest request) {
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			String vendorid = request.getParameter("vendorid");
			if (vendorid != null && !vendorid.equals("")) {
				Vendor vendor = vendorService.findByVendorid(Long.parseLong(vendorid.toString()));
				vendor.setBalance("" + WebAPICall.getVendorBalance(vendor.getBcaddress()));
				model.addAttribute("vendor", vendor);
				List<User> users = userService.findByVendor(vendor);
				model.addAttribute("users", users);
				VendorQRCode qrCode = vendorQrCodeService.findByVendor(vendor);
				if (qrCode != null) {
					StringBuilder sb = new StringBuilder();
					sb.append("data:image/png;base64,");
					sb.append(StringUtils.newStringUtf8(Base64.encodeBase64(qrCode.getCode(), false)));
					model.addAttribute("qrcode", sb.toString());
				} else {
					String noqrCode = "data:image/png;base64,";
					model.addAttribute("qrcode", noqrCode);
				}
				
				return "viewvendordetail";
			}
			
		    return "listvendors";
		} else {
			return "login";
		}
	}
	
	@RequestMapping(value = "/sendCurreny", method = RequestMethod.POST)
	public ModelAndView sendCurreny(Model model, HttpServletRequest httpServletRequest) {

		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			String userid = httpServletRequest.getParameter("userid");
			String amount = httpServletRequest.getParameter("amount");
			String toaddress = httpServletRequest.getParameter("storeName");
			String description = httpServletRequest.getParameter("description");
			User user = userService.findByUserid(Long.parseLong(userid));
			Transaction transaction = new Transaction();
			transaction.setUser(user);
			transaction.setCutomeraddress(toaddress);
			transaction.setTransactionamount(amount);
			transaction.setTransactiondate(new Date());
			transaction.setTransactiondescription(description);
			transaction.setTransactionaddress(WebAPICall.payToStore(user.getBcaddress(), toaddress, amount));
			transactionService.addTransaction(transaction);
			
			return new ModelAndView("redirect:/shop");	
		}
		return new ModelAndView("redirect:/login");
	}
	
	@RequestMapping(value = "/viewqrcode", method = RequestMethod.POST)
	public String viewqrcode(Model model, HttpServletRequest request, HttpServletResponse response) {
		String userId = request.getParameter("userId");
		User user = userService.findByUserid(Long.parseLong(userId.toString()));
		user.setBalance("" + WebAPICall.getCustomerBalance(user.getBcaddress()));
		List<Transaction> transactions = transactionService.findByUser(user);
		model.addAttribute("myTransactions", transactions);
		model.addAttribute("currentUser", user);
		return "shop";
	}
	
	@RequestMapping(value = "/addNewVendor", method = RequestMethod.POST) 
	public String addNewVendor(Model model, HttpServletRequest httpServletRequest) {
		
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			String vendorname = httpServletRequest.getParameter("vendorname");
			String description = httpServletRequest.getParameter("description");
			String address = httpServletRequest.getParameter("address");
			String contactNumber = httpServletRequest.getParameter("contactNumber");
			String email = httpServletRequest.getParameter("email");
			String website = httpServletRequest.getParameter("website");
			String storepercentage = httpServletRequest.getParameter("storepercentage");
			String customerpercentage = httpServletRequest.getParameter("customerpercentage");
			String typeid = httpServletRequest.getParameter("userType");
			String initialBalance = httpServletRequest.getParameter("initialBalance");
			
			Vendor availableVendor = vendorService.findByVendorname(vendorname);
			if (availableVendor == null) {
				Vendor vendor = new Vendor();
				vendor.setVendorname(vendorname);
				vendor.setDescription(description);
				vendor.setAddress(address);
				vendor.setContactnumber(contactNumber);
				vendor.setEmail(email);
				vendor.setWebsite(website);
				Type type = typeService.findByTypeid(Long.parseLong(typeid));
				vendor.setType(type);
				String bcAddress = WebAPICall.registerNewUser(vendorname);
				vendor.setBcaddress(bcAddress);
				
				Integer charge = 0;
				if (type.getName().equals("Customer") && !customerpercentage.isEmpty()) {
					charge = Integer.parseInt(customerpercentage);
					vendor.setCustomerpercentage(Double.parseDouble(customerpercentage));
				} else if (type.getName().equals("Store") && !storepercentage.isEmpty()) {
					charge = Integer.parseInt(storepercentage);
					vendor.setStorepercentage(Double.parseDouble(storepercentage));
				}
				
				boolean result = WebAPICall.onBoardVendor(vendorname, bcAddress, description, charge, Integer.parseInt(initialBalance));
				if (result)
					vendorService.addVendor(vendor);
				
				byte[] qrCode = QRCodeProcessor.generateQRCode(bcAddress);
				VendorQRCode code = new VendorQRCode();
				code.setVendor(vendorService.findByVendorname(vendorname));
				code.setCode(qrCode);
				vendorQrCodeService.addVendorQRCode(code);
				
			} else {
				model.addAttribute("errormsg", "Vendor name is already available. Please try again.");
				return "addvendor";	
			}
			return "redirect:/listvendors";
		}
		return "redirect:/login";
	}
	
	
	@RequestMapping(value = "/registerUser", method = RequestMethod.POST)
	public String registerUser(Model model, HttpServletRequest httpServletRequest) {
		
		String typeid = httpServletRequest.getParameter("userType");
		String userName = httpServletRequest.getParameter("userName");
		String password = httpServletRequest.getParameter("password");
		//String confirmPassword = httpServletRequest.getParameter("confirmPassword");
		String fullName = httpServletRequest.getParameter("fullName");
		String address = httpServletRequest.getParameter("address");
		String contactNumber = httpServletRequest.getParameter("contactNumber");
		String email = httpServletRequest.getParameter("email");
		String website = httpServletRequest.getParameter("website");
		String cvendorid = httpServletRequest.getParameter("cvendorId");
		String svendorid = httpServletRequest.getParameter("svendorId");
		String identifier = httpServletRequest.getParameter("identifier");
		String storeNumber = httpServletRequest.getParameter("storeNumber");
		String initialBalance = httpServletRequest.getParameter("initialBalance");
		
		List<Type> types = typeService.findAllType();
		model.addAttribute("types", types);
		
		User availableUser = userService.findByUsername(userName);
		if (availableUser == null) {
			User user = new User();
			user.setUsername(userName);
			user.setPassword(password);
			user.setFullname(fullName);
			user.setAddress(address);
			user.setContactnumber(contactNumber);
			user.setEmail(email);
			user.setWebsite(website);
			user.setUnlocked(true);
			Type type = typeService.findByTypeid(Long.parseLong(typeid));
			user.setType(type);
			
			
			String bcAddress = WebAPICall.registerNewUser(password);
			user.setBcaddress(bcAddress);
			
			boolean result = false;
			
			if (type.getName().equals("Customer") && !cvendorid.isEmpty()) {
				Vendor vendor = vendorService.findByVendorid(Long.parseLong(cvendorid));
				user.setVendor(vendor);
				result = WebAPICall.onBoardCustomer(userName, bcAddress, fullName, identifier, Integer.parseInt(initialBalance));
			} else if (type.getName().equals("Store") && !svendorid.isEmpty()) {
				Vendor vendor = vendorService.findByVendorid(Long.parseLong(svendorid));
				user.setVendor(vendor);
				result = WebAPICall.onBoardStore(userName, bcAddress, fullName, storeNumber, Integer.parseInt(initialBalance));
			}
			
			if (result) {
				WebAPICall.associateVendor(bcAddress, user.getVendor().getBcaddress());
				userService.addUser(user);
				
				byte[] qrCode = QRCodeProcessor.generateQRCode(bcAddress);
				QRCode code = new QRCode();
				code.setUser(userService.findByUsername(user.getUsername()));
				code.setCode(qrCode);
				qrCodeService.addQRCode(code);
				
				model.addAttribute("registered", "You have successfully registered...!");
			} else {
				model.addAttribute("registered", "Issue in on-board user, Please try again!");
			}
			
		} else {
			model.addAttribute("registered", "Username is not available, Please try again!");
		}
		
		return "registration";
	}
	
	@RequestMapping(value = "/login", method = RequestMethod.GET)
	public String loginScreen(Model model) {
	    return "login";
	}
	
	@RequestMapping(value = "/logout", method = RequestMethod.GET)
	public String logoutScreen(Model model) {
		HttpSession session = getSession();
		session.removeAttribute("loginUser");
	    return "home";
	}
	
	public HttpSession getSession() {
		if (request != null) {
			return request.getSession();
		}
		return null;
	}
	
	@RequestMapping(value = "/loginUser", method = RequestMethod.POST)
	public ModelAndView loginUser(Model model, HttpServletRequest httpServletRequest) {
		Date loginTime = new Date();
		String userName = httpServletRequest.getParameter("userName");
		String password = httpServletRequest.getParameter("password");
		
		User user = userService.loginUser(userName, password);
		if (user != null) {
			
			HttpSession session = getSession();
			session.setAttribute("loginUser", user.getUserid());
			
			if (user.getIsadminuser() != null && user.getIsadminuser()) {
				return new ModelAndView("redirect:/listvendors");
			} else {
				/*if (user.getUnlocked() == null || !user.getUnlocked()) {
				Boolean status = WebAPICall.unlockUser(user.getBcaddress(), password);
				user.setUnlocked(status);
			}*/
			user.setLastLogin(user.getCurrentLogin());
			user.setCurrentLogin(loginTime);
			userService.addUser(user);
			
			return new ModelAndView("redirect:/shop");
			}
		} 
		
		model.addAttribute("errormsg", "Invalid user name or password. Please try again.");
		return new ModelAndView("redirect:/login");	
		
	}
	
	@RequestMapping(value = "/shop", method = RequestMethod.GET)
	public String transactionListScreen(Model model) {
	    
		HttpSession session = getSession();
		Object userId = session.getAttribute("loginUser");
		
		if(userId != null) {
			User user = userService.findByUserid(Long.parseLong(userId.toString()));
			if (user.getType().getName().equals("Customer")) {
				user.setBalance("" + WebAPICall.getCustomerBalance(user.getBcaddress()));
				List<Transaction> transactions = transactionService.findByUser(user);
				model.addAttribute("myTransactions", transactions);
			} else if (user.getType().getName().equals("Store")) {
				user.setBalance("" + WebAPICall.getStoreBalance(user.getBcaddress()));
				List<Transaction> transactions = transactionService.findByCutomeraddress(user.getBcaddress());
				model.addAttribute("myTransactions", transactions);
			}
			
			
			model.addAttribute("currentUser", user);
			
			List<User> stores = userService.findByType(typeService.findByName("Store"));
			model.addAttribute("stores", stores);
			
			QRCode qrCode = qrCodeService.findByUser(user);
			if (qrCode != null) {
				StringBuilder sb = new StringBuilder();
				sb.append("data:image/png;base64,");
				sb.append(StringUtils.newStringUtf8(Base64.encodeBase64(qrCode.getCode(), false)));
				model.addAttribute("qrcode", sb.toString());
			} else {
				String noqrCode = "data:image/png;base64,";
				model.addAttribute("qrcode", noqrCode);
			}
			
			return "shop";
		}
		
		return "login";
	}
}
